def longestPalindrome(s: str) -> int:
    from collections import Counter
    
    count = 0
    oddVal = False
    for i in Counter(s).items():
        if i[1] % 2 == 0:
            count += i[1]
        else:
            count += i[1] - 1
            oddVal = True
    if oddVal:
        return count + 1
    return count

